package nikitaApplication;

public class incrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int x = 3;
		    x++;                      //increment
		    System.out.println(x);
		    int y = 3;
		    y--;                       //decrement
		    System.out.println(y);

	}

}
